select a.id as "id", a.family_id as "family_id", a.name as "name", a.gorder as "gorder", a.seniority as "seniority", a.order_by as "order_by", a.status as "status", a.remark as "remark", a.create_user as "create_user", a.create_organize as "create_organize", a.create_org_cascade as "create_org_cascade", a.create_time as "create_time", a.update_time as "update_time" from gen_generation a where 1=1
and a.family_id = #{family_id}
and a.status = #{status}
and a.name like concat('%', #{name},'%')
order by a.order_by+0 asc, a.create_time desc
