			and ( ${param} )

select a.id as "id", a.family_id as "family_id", a.generation_id as "generation_id", a.father_id as "father_id", a.mother_id as "mother_id", a.name as "name", a.content as "content", a.tpdz as "tpdz", a.level as "level", a.order_by as "order_by", a.open_wife as "open_wife", a.remark as "remark", a.create_user as "create_user", a.create_organize as "create_organize", a.create_org_cascade as "create_org_cascade", a.create_time as "create_time", a.update_time as "update_time", b.name as "father_name", c.name as "generation_name" from gen_member a left join gen_member b on a.father_id=b.id left join gen_generation c on a.generation_id=c.id where 1=1
and a.name like concat('%', #{name},'%')
and a.family_id = #{family_id}
and a.generation_id = #{generation_id}
and a.father_id = #{father_id}
and a.mother_id = #{mother_id}
order by a.order_by+0 asc, a.create_time desc
