mapper.select:
select
	a.id,
	concat(c.name,'：',a.name,'(',sd.name,')') as "name",
	IFNULL(concat('家属：',b.title),concat('家属：无')) as "title",
	IFNULL(d.child_num,concat('家属：无')) as "title",
	IF(d.child_num>0,'111','110') as "relationship"
	from gen_member a
	left join gen_generation c on a.generation_id=c.id
	left join (
	select GROUP_CONCAT(name) as "title",member_id from gen_member_wife group by member_id
	) b on a.id=b.member_id
	left join (
	select father_id,count(*) as "child_num" from gen_member group by father_id
	) d on a.id=d.father_id
	left join system_dictionary sd on a.level=sd.id
	where 1=1

		and a.name like concat('%', #{name},'%')


		and a.id != #{exclude_id}


		and a.family_id = #{family_id}


		and a.generation_id = #{generation_id}


		and a.father_id = #{father_id}


		and a.mother_id = #{mother_id}

	order by a.order_by+0 asc, a.create_time desc

select a.id, concat(c.name,'：',a.name,'(',sd.name,')') as "name", IFNULL(concat('家属：',b.title),concat('家属：无')) as "title", IFNULL(d.child_num,0) as "child_num", IF(d.child_num>0,'111','110') as "relationship", IFNULL(g.name,'无') as "father_name" from gen_member a left join gen_generation c on a.generation_id=c.id left join ( select GROUP_CONCAT(name) as "title",member_id from gen_member_wife group by member_id ) b on a.id=b.member_id left join ( select father_id,count(*) as "child_num" from gen_member group by father_id ) d on a.id=d.father_id left join gen_member g on a.father_id=g.id left join system_dictionary sd on a.level=sd.id where 1=1
and a.name like concat('%', #{name},'%')
and a.generation_id = #{generation_id}
order by a.order_by+0 asc, a.create_time desc
limit #{startPage},#{endPage}
