mapper.select:
select a.id as "id", a.classify as "classify", a.menu_cascade as "menu_cascade", a.name as "name", a.code as "code", a.parent_id as "parent_id", a.url as "url", a.icon as "icon", a.type as "type", a.level as "level", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.update_time as "update_time" from system_menu as a where 1=1
and a.name like concat('%', #{pd.name},'%')
and a.code like concat('%', #{pd.code},'%')
and a.parent_id = #{pd.parent_id}
and a.type = #{pd.type}
and a.create_user = #{pd.create_user}
and a.create_organize = #{pd.create_organize}
and a.create_org_cascade = #{pd.create_org_cascade}
and ( ${param} )
order by a.order_by+0 asc, a.create_time desc

select
	count(*)
	from system_menu as a
	where 1=1

		and a.name like concat('%', #{pd.name},'%')


		and a.code like concat('%', #{pd.code},'%')


		and a.parent_id = #{pd.parent_id}


		and a.type = #{pd.type}



		and a.create_user = #{pd.create_user}


		and a.create_organize = #{pd.create_organize}


		and a.create_org_cascade = #{pd.create_org_cascade}



			and ( ${param} )

select a.id as "id", a.classify as "classify", a.menu_cascade as "menu_cascade", a.name as "name", a.code as "code", a.parent_id as "parent_id", a.url as "url", a.icon as "icon", a.type as "type", a.level as "level", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.update_time as "update_time" from system_menu as a where 1=1
and a.name like concat('%', #{name},'%')
and a.code like concat('%', #{code},'%')
and a.parent_id = #{parent_id}
and a.type = #{type}
order by a.order_by+0 asc, a.create_time desc

select a.id as "id", a.name as "title", a.url as "href", a.icon as "icon", a.code as "code", c.id as "user_auth_id", c.type as "user_auth_type", case when c.organize_ids='undefined' then '' else c.organize_ids end, case when c.organize_names='undefined' then '' else c.organize_names end from system_menu as a left join system_role_menu b on a.id=b.menu_id left join system_user_auth c on a.id=c.menu_id and c.user_id=#{user_id} where b.role_id in ( select role_id from system_user_role where user_ids like concat('%,',#{user_id},'%') or organize_ids like concat('%,',#{organize_id},'%') )
and a.parent_id = #{parent_id}
and a.type = #{type}
and a.level = #{level}
and a.classify = #{classify}
group by a.id order by a.order_by+0 asc, a.create_time desc
